body {
  width: 100%;
  margin: 0;
  background-color: #181f2a;
  color: white;
}

a {
  text-decoration: none;
}

ul {
  padding: 0;
}

li {
  list-style: none;
}

body > header {
  display: flex;
  justify-content: space-between;
  width: 90%;
  margin-left: auto;
  margin-right: auto;
}
body > header img {
  width: 100px;
}
body > header ul {
  display: block;
}
body > header ul li {
  display: inline;
  margin-left: 1rem;
}
body > header ul li a {
  color: white;
}

@media (min-width: 375px) {
  body > header img {
    width: 20%;
  }
  body > header ul li {
    font-size: 30px;
    margin-left: 3rem;
  }
}
main article {
  text-align: center;
}
main article header {
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
}
main article header img {
  width: 100%;
}
main article header h2 {
  font-size: 28px;
}
main article button {
  background-color: #339ecc;
  border: none;
  border-radius: 30px;
  width: 300px;
  height: 55px;
  text-align: center;
  color: white;
}

@media (min-width: 375px) {
  main > article header {
    width: 50%;
  }
  main > article header img {
    width: 100%;
    padding-top: 4rem;
  }
  main > article header h2 {
    font-size: 40px;
  }
  main > article header p {
    width: 62%;
    margin: auto;
    padding-bottom: 2rem;
  }
}
main article article {
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  padding-top: 4rem;
}
main article article u {
  color: #339ecc;
}

@media (min-width: 375px) {
  .content {
    display: grid;
    grid-template-areas: "access security" "collaborate file";
    width: 60%;
    margin: auto;
  }
  .content article {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .content article h3 {
    margin: 0;
  }
  .content .access {
    grid-area: access;
  }
  .content .security {
    grid-area: security;
  }
  .content .collaborate {
    grid-area: collaborate;
  }
  .content .file {
    grid-area: file;
  }

  .productive {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  .productive img {
    width: 50%;
  }
  .productive div {
    width: 40%;
  }
}
article section {
  text-align: start;
  width: 90%;
  margin-right: auto;
  margin-left: auto;
}
article section img {
  width: 100%;
}

article nav {
  width: 80%;
  margin-left: auto;
  margin-right: auto;
}
article nav li {
  background-color: #1c2431;
  display: flex;
  border-radius: 10px;
  flex-direction: column-reverse;
  text-align: start;
  margin-bottom: 2rem;
  padding: 1rem;
}
article nav li img {
  width: 50px;
  border-radius: 50%;
  margin-right: 1rem;
}
article nav li .comment {
  display: block;
}
article nav li .comment div {
  display: inline-block;
}

@media (min-width: 375px) {
  article nav ul {
    display: flex;
    justify-content: space-between;
  }
  article nav ul li {
    margin-right: 1rem;
  }
}
.get-access {
  width: 90%;
  margin-right: auto;
  margin-left: auto;
  background-color: #1c2431;
  text-align: center;
  padding: 0.5rem;
  padding-bottom: 2rem;
  border-radius: 10px;
}
.get-access p {
  width: 85%;
  margin: auto;
  padding-bottom: 2rem;
}
.get-access input {
  width: 300px;
  height: 50px;
  border-radius: 30px;
  margin-bottom: 1rem;
}

@media (min-width: 375px) {
  .get-access {
    width: 60%;
  }
  .get-access p {
    width: 70%;
  }
  .get-access input {
    width: 400px;
  }
  .get-access button {
    width: 200px;
  }
}
footer {
  width: 90%;
  margin-left: auto;
  margin-right: auto;
  padding-top: 4rem;
}
footer a {
  color: white;
}
footer .address {
  display: block;
}
footer .address a {
  display: list-item;
  color: white;
  padding-bottom: 1.5rem;
}
footer .address .location {
  width: 89%;
  margin-left: auto;
}
footer .address a[href^=loaction]::before {
  content: url(/166af3a49584cf7297523bcdfa295aa5.svg);
  margin-right: 1rem;
  position: absolute;
  left: 1rem;
}
footer .address a[href^=tel]::before {
  content: url(/519d76a84cf1bce79553e08acc84a2ca.svg);
  margin-right: 1rem;
}
footer .address a[href^=mailto]::before {
  content: url(/2dad700db1cb4174fdff42d6e16cbdce.svg);
  margin-right: 1rem;
}

@media (min-width: 375px) {
  footer nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  footer nav .address .location {
    width: 100%;
    margin-left: 0;
  }
  footer nav .address a[href^=loaction]::before {
    margin-right: 1rem;
    position: inherit;
  }
}

